import{_ as e,c as t,a0 as o,o as s}from"./chunks/framework.C8sLidy6.js";const p=JSON.parse('{"title":"1Panel 安装运行 xiaomusic 教程","description":"","frontmatter":{"title":"1Panel 安装运行 xiaomusic 教程"},"headers":[],"relativePath":"issues/600.md","filePath":"issues/600.md","lastUpdated":1763563726000}'),r={name:"issues/600.md"};function i(n,a,l,c,u,h){return s(),t("div",null,a[0]||(a[0]=[o('<h1 id="_1panel-安装运行-xiaomusic-教程" tabindex="-1">1Panel 安装运行 xiaomusic 教程 <a class="header-anchor" href="#_1panel-安装运行-xiaomusic-教程" aria-label="Permalink to &quot;1Panel 安装运行 xiaomusic 教程&quot;" target="_self">​</a></h1><h2 id="_1panel-安装运行" tabindex="-1">1Panel 安装运行 <a class="header-anchor" href="#_1panel-安装运行" aria-label="Permalink to &quot;1Panel 安装运行&quot;" target="_self">​</a></h2><p>本教程将指导您如何在 1Panel 面板中安装和配置 XiaoMusic。</p><h3 id="前提条件" tabindex="-1">前提条件 <a class="header-anchor" href="#前提条件" aria-label="Permalink to &quot;前提条件&quot;" target="_self">​</a></h3><p>在开始安装前，请确保您已完成以下准备工作：</p><ul><li>已安装并运行 1Panel 面板</li></ul><p>如需详细安装 1Panel 面板，请参考 <a href="https://1panel.cn/docs/v2/installation/online_installation/" target="_self">1Panel 官方安装文档</a></p><p>安装完成后，通过提示的<strong>访问地址</strong>和<strong>初始账号密码</strong>登录 1Panel。</p><h3 id="安装-xiaomusic" tabindex="-1">安装 XiaoMusic <a class="header-anchor" href="#安装-xiaomusic" aria-label="Permalink to &quot;安装 XiaoMusic&quot;" target="_self">​</a></h3><p>登录 1Panel，进入 <strong>应用商店</strong>，搜索 <strong>xiaomusic</strong>，点击<strong>安装</strong>即可。</p><h4 id="配置参数说明" tabindex="-1">配置参数说明 <a class="header-anchor" href="#配置参数说明" aria-label="Permalink to &quot;配置参数说明&quot;" target="_self">​</a></h4><p>安装时请根据实际需求配置以下参数：</p><ul><li><strong>高级设置</strong>：根据实际需要勾选<strong>端口外部访问</strong>，勾选后即可通过外部 IP 访问 XiaoMusic。</li></ul><blockquote><p>保持<strong>默认配置</strong>也可以完成安装，但建议根据实际需求调整。</p></blockquote><h3 id="访问-xiaomusic" tabindex="-1">访问 XiaoMusic <a class="header-anchor" href="#访问-xiaomusic" aria-label="Permalink to &quot;访问 XiaoMusic&quot;" target="_self">​</a></h3><p>安装完成后，进入 1Panel 的 <strong>已安装</strong> 页面，点击 <strong>跳转</strong> 即可进入 XiaoMusic 的 <strong>WebUI</strong> 页面。</p><blockquote><p>使用前建议在 <strong>面板设置</strong> 页面设置好<strong>默认访问地址</strong>。</p></blockquote><blockquote><p>如果后续配置了<strong>反向代理</strong>，可以在 <code>已安装 → 参数</code> 页面修改 <strong>Web 访问地址</strong>。</p></blockquote><h2 id="评论" tabindex="-1">评论 <a class="header-anchor" href="#评论" aria-label="Permalink to &quot;评论&quot;" target="_self">​</a></h2><p>没有评论。 <a href="https://github.com/hanxi/xiaomusic/issues/600" target="_self">链接到 GitHub Issue</a></p>',20)]))}const d=e(r,[["render",i]]);export{p as __pageData,d as default};
